import React, { Component } from "react";
import { Grid, Row, Col, Table } from "react-bootstrap";

import Card from "components/Card/Card.jsx";
import { thArray, tdArray } from "variables/Variables.jsx";

class Trucks extends Component {

  constructor(){
    super();
    this.state = {
      trucks : {}
    }


  }

  componentWillMount(){
    //fetch data for displaying truck
    var obj = {
      method: 'GET' ,
      mode : 'no-cors',
      headers: {
          'Access-Control-Request-Headers': 'Authorization',
          'Authorization': 'bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..eJn_0-H5C3OqpQ-2fSMvmiWP6l5LfwjIS7Xp5xv-p7M',
          'Content-Type': 'application/json',
          'Origin': ''
      },
      credentials: 'include'
    };

    fetch('http://localhost/TmsId.Api/api/LafargeTms/getTrucks', obj)
    .then(response => response.json())
    .then(data => {
      this.setState({
        trucks:data.result
      })
    });
  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={12}>
              <Card
                title="Lafarge Trucks"
                category="Trucks"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table striped hover>
                    <thead>
                      <tr>
                        {thArray.map((prop, key) => {
                          return <th key={key}>{prop}</th>;
                        })}
                      </tr>
                    </thead>
                    <tbody>
                      {tdArray.map((prop, key) => {
                        return (
                          <tr key={key}>
                            {prop.map((prop, key) => {
                              return <td key={key}>{prop}</td>;
                            })}
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Trucks;
