import React, { Component } from "react";
import { Grid, Row, Col, Table } from "react-bootstrap";

import { Card, FormGroup} from "components/Card/Card.jsx";
import Button from "components/CustomButton/CustomButton.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";


class lafargeLocator extends Component {

  constructor(){
    super();
    this.handleChange = this.handleChange.bind(this);
    this.findLocation = this.findLocation.bind(this);
    this.state =
    {
      dLongitude : "",
      dLatitude :"", 
      dAssetId : "",

      xAsset:"",
      xDriver:""
    }
  }

  handleChange(e){
    this.setState(
      {
        dLongitude : e.target.value,
        dLatitude :e.target.value, 
        dAssetId : e.target.value,
        xAsset:e.target.value,
        xDriver:e.target.value
      }
    );
  }

  findLocation()
  {
    var assetid = document.getElementById('xDriver').value;
    var driverid = document.getElementById('xAsset').value;

    alert(assetid);
  }

  findDistance(){

  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={4}>
              <Card
                title="Locate Distance"
                category="Find distance by using point (Long, Lat)"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <div className="content">
                  <form>
                    <FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Longitude",
                          type: "text",
                          name:"dLongitude",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false
                        }
                      ]}
                    />

<FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Latitude",
                          type: "text",
                          name:"dLatitude",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false
                        }
                      ]}
                    />

<FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Asset ID",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false
                        }
                      ]}
                    />

                    <Button bsStyle="danger" pullRight fill type="submit">
                      Find Distance
                    </Button>

                    <div className="clearfix" />

                  </form>
                  </div>
                }
              />
            </Col>
            <Col md={6}>
            <Card
                title={`Distance of Truck Asset ID: (${this.state.dAssetId}) from Point (${this.state.dLongitude},${this.state.dLatitude})`}
                category=""
                ctTableFullWidth
                ctTableResponsive
                onChange={this.handleChange}
                content={
                  <Table hover>
                    <thead>
                      <tr>
                          <th>Distance From (Long/lat)</th>
                      </tr>
                    </thead>
                    <tbody>
                    <tr>
                          <td>399999km</td>
                      </tr>
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={4}>
              <Card
                title="Find Location"
                category="Location by Asset & Driver"
                ctTableFullWidth
                ctTableResponsive
                  content={
                    <div className="content">
                    <form>
                      <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                          {
                            label: "Truck/Asset",
                            name:"xAssetId",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "",
                            defaultValue: "",
                            disabled: false
                          }
                        ]}
                      />

  <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                          {
                            label: "driver",
                            name:"xDriver",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "",
                            defaultValue: "",
                            disabled: false
                          }
                        ]}
                      />

                      <Button bsStyle="danger" pullRight fill type="submit" onChange={this.state.findLocation}>
                        Find Distance
                      </Button>

                      <div className="clearfix" />

                    </form>
                    </div>
                  }
              />
            </Col>
            <Col md={6}>
              <Card
                title="Find Location"
                category="Location by Asset & Driver"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table hover>
                    <thead>
                      <tr>
                          <th>Address</th>
                          <th>Point (Long,Lat)</th>
                          <th>Timestamp</th>
                      </tr>
                    </thead>
                    <tbody>
                    <tr>
                          <td>5, Osato Street</td>
                          <td>677.99, 88557.9</td>
                          <td>677.99, 88557.9</td>
                      </tr>
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>

        </Grid>
      </div>
    );
  }
}

export default lafargeLocator;
