import React, { Component } from "react";
import { Grid, Row, Col, Table } from "react-bootstrap";

import Card from "components/Card/Card.jsx";
import { thArray, tdArray } from "variables/Variables.jsx";

class Trucks extends Component {

  constructor()
  {
   super();
   this.state = {
     data: null,
     }
     this.getData();
   }

   getData()
   {
     let data= fetch('http://localhost/TmsId.Api/api/LafargeTms/getTrucks').then((resp) =>{
       resp.json().then((res)=>{
       console.log(res.meta);
       this.setState({data:res.view})
       })
     })
   }


  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={12}>
              <Card
                title="Lafarge Trucks"
                category="Trucks"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table striped hover>
                    <thead>
                      <tr>
                        {this.state.items}
                        {this.state.items.map((prop, key) => {
                          return <th key={key}>{prop}</th>;
                        })}
                      </tr>
                    </thead>
                    <tbody>
                      {tdArray.map((prop, key) => {
                        return (
                          <tr key={key}>
                            {prop.map((prop, key) => {
                              return <td key={key}>{prop}</td>;
                            })}
                          </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Trucks;
