import React, { Component } from "react";
import { Grid, Row, Col, Table } from "react-bootstrap";

import { Card, FormGroup} from "components/Card/Card.jsx";
import Button from "components/CustomButton/CustomButton.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";


class lafargeLocator extends Component {

  constructor(){
    super();
    this.handleChange = this.handleChange.bind(this);
    this.findLocation = this.findLocation.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {value: ''};

  }

  handleChange(event) {
    this.setState({value: event.target.value});
    alert(this.state.value);
  }

  findLocation()
  {
    // var assetid = document.getElementById('xAssetId').value;
    // var driverid = document.getElementById('xDriver').value;

    const apiUrl = 'http://localhost/TmsId.Api/api/LafargeTms/getDrivers';
    fetch(apiUrl, {
      method: 'POST',
      headers:{
        'X-Requested-With': 'XMLHttpRequest'
      }
    })
      .then((response) => response.json())
      .then((a) => {

        const result = Object.values(a.result);
        const arr = [];

        for(var i =0; i < result.length; i++){
          var subarray = [];
          for (const prop in result[i]) {
            subarray.push(result[i][prop]);
          }
          arr.push(subarray);
        }

        this.setState({data:arr, heading:["Id", "Created At", "Updated At", "Site Id", "Driver Id", "Name", "Employee Number"]})
      });

    alert(2);
  }

  findDistance(){

  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={4}>
              <Card
                title="Locate Distance"
                category="Find distance by using point (Long, Lat)"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <div className="content">
                  <form>
                    <FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Longitude",
                          type: "text",
                          name:"dLongitude",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false,
                        }
                      ]}
                    />

<FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Latitude",
                          type: "text",
                          name:"dLatitude",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false,
                        }
                      ]}
                    />

<FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Asset ID",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false, 
                          onChange: this.handleChange
                        }
                      ]}
                    />

                    <Button bsStyle="danger" pullRight fill type="submit">
                      Find Distance
                    </Button>

                    <div className="clearfix" />

                  </form>
                  </div>
                }
              />
            </Col>
            <Col md={6}>
            <Card
                title={`Distance of Truck Asset ID: (${this.state.value}) from Point (${this.state.dLongitude},${this.state.dLatitude})`}
                category=""
                ctTableFullWidth
                ctTableResponsive
                onChange={this.handleChange}
                content={
                  <Table hover>
                    <thead>
                      <tr>
                          <th>Distance From (Long/lat)</th>
                      </tr>
                    </thead>
                    <tbody>
                    <tr>
                          <td>399999km</td>
                      </tr>
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={4}>
              <Card
                title="Find Location"
                category="Location by Asset & Driver"
                ctTableFullWidth
                ctTableResponsive
                  content={
                    <div className="content">
                    <form>
                      <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                          {
                            label: "Truck/Asset",
                            name:"xAssetId",
                            type: "text",
                            id:"xDriver",
                            bsClass: "form-control",
                            placeholder: "",
                            defaultValue: "",
                            disabled: false
                          }
                        ]}
                      />

  <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                          {
                            label: "driver",
                            name:"xDriver",
                            id:"xDriver",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "",
                            defaultValue: "",
                            disabled: false
                          }
                        ]}
                      />

                      <Button bsStyle="danger" pullRight fill type="button" onClick={this.findLocation}>
                        Find Distance
                      </Button>

                      <div className="clearfix" />

                    </form>
                    </div>
                  }
              />
            </Col>
            <Col md={6}>
              <Card
                title="Find Location"
                category="Location by Asset & Driver"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table hover>
                    <thead>
                      <tr>
                          <th>Address</th>
                          <th>Point (Long,Lat)</th>
                          <th>Timestamp</th>
                      </tr>
                    </thead>
                    <tbody>
                    <tr>
                          <td>5, Osato Street</td>
                          <td>677.99, 88557.9</td>
                          <td>677.99, 88557.9</td>
                      </tr>
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>

        </Grid>
      </div>
    );
  }
}

export default lafargeLocator;
