import React, { Component } from "react";
import { Grid, Row, Col, Table } from "react-bootstrap";

import { Card, FormGroup} from "components/Card/Card.jsx";
import Button from "components/CustomButton/CustomButton.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";


class lafargeLocator extends Component {

  constructor(){
    super();
    this.handleChange = this.handleChange.bind(this);
    this.findLocation = this.findLocation.bind(this);

    this.state = {
      dAssetId: '',
      dLongitude: '',
      dLatitude: '',
      xDriver:'',
      xAssetId:'',
      address:'',
      point:'',
      Timestamp:'',
      distance:''
    };

  }

  handleChange(event) 
  {
    this.setState({value: event.target.value});
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({[nam]: val});
  }

  findLocation()
  {
    var assetid = this.state.xAssetId;
    var driverid = this.state.xDriver;

    const requestOptions = {
      method: 'POST',
      headers: { 
          'Content-Type': 'application/json',
          'Authorization': 'Bearer my-token',
          'My-Custom-Header': 'foobar'
      },
      body: JSON.stringify({ DriverId:driverid, AssetId: assetid})
  };
  fetch('http://localhost/TmsId.Api/api/LafargeTms/getDriverLocationByDriver', requestOptions)
      .then(async response => {
          const data = await response.json();

          // check for error response
          if (!response.ok) {
              // get error message from body or default to response status
              const error = (data && data.message) || response.status;
              return Promise.reject(error);
          }

          this.setState({ address: data.CurrentAddress, point: data.Longitude+''+data.Latitude, Timestamp: data.id })
      })
      .catch(error => {
          this.setState({ errorMessage: error.toString() });
          console.error('There was an error!', error);
      });
  }

  findDistance(){

  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={4}>
              <Card
                title="Locate Distance"
                category="Find distance by using point (Long, Lat)"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <div className="content">
                  <form>
                    <FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Longitude",
                          type: "text",
                          name:"dLongitude",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false,
                          onChange:this.handleChange
                        }
                      ]}
                    />

<FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Latitude",
                          type: "text",
                          name:"dLatitude",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false,
                          onChange:this.handleChange
                        }
                      ]}
                    />

<FormInputs
                      ncols={["col-md-12"]}
                      properties={[
                        {
                          label: "Asset ID",
                          type: "text",
                          name:"dAssetId",
                          bsClass: "form-control",
                          placeholder: "",
                          defaultValue: "",
                          disabled: false, 
                          onChange: this.handleChange
                        }
                      ]}
                    />

                    <Button bsStyle="danger" pullRight fill type="submit">
                      Get Distance
                    </Button>

                    <div className="clearfix" />

                  </form>
                  </div>
                }
              />
            </Col>
            <Col md={6}>
            <Card
                title={`Distance of Truck Asset ID: (${this.state.dAssetId}) from Point (${this.state.dLongitude},${this.state.dLatitude})`}
                category=""
                ctTableFullWidth
                ctTableResponsive
                onChange={this.handleChange}
                content={
                  <Table hover>
                    <thead>
                      <tr>
                          <th>Distance From (Long/lat)</th>
                      </tr>
                    </thead>
                    <tbody>
                    <tr>
                <td>{this.state.distance}</td>
                      </tr>
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={4}>
              <Card
                title="Find Location"
                category="Location by Asset & Driver"
                ctTableFullWidth
                ctTableResponsive
                  content={
                    <div className="content">
                    <form>
                      <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                          {
                            label: "Truck/Asset",
                            name:"xAssetId",
                            type: "text",
                            id:"xDriver",
                            bsClass: "form-control",
                            placeholder: "",
                            defaultValue: "",
                            disabled: false,
                            onChange:this.handleChange
                          }
                        ]}
                      />

  <FormInputs
                        ncols={["col-md-12"]}
                        properties={[
                          {
                            label: "driver",
                            name:"xDriver",
                            id:"xDriver",
                            type: "text",
                            bsClass: "form-control",
                            placeholder: "",
                            defaultValue: "",
                            disabled: false,
                            onChange:this.handleChange
                          }
                        ]}
                      />

                      <Button bsStyle="danger" pullRight fill type="button" onClick={this.findLocation}>
                        Find Location
                      </Button>

                      <div className="clearfix" />

                    </form>
                    </div>
                  }
              />
            </Col>
            <Col md={6}>
              <Card
                title="Location"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table hover>
                    <thead>
                      <tr>
                          <th>Address</th>
                          <th>Point (Long,Lat)</th>
                          <th>Timestamp</th>
                      </tr>
                    </thead>
                    <tbody>
                    <tr>
                <td>{this.state.address}</td>
                <td>{this.state.point}</td>
                <td>{this.state.Timestamp}</td>
                      </tr>
                    </tbody>
                  </Table>
                }
              />
            </Col>
          </Row>

        </Grid>
      </div>
    );
  }
}

export default lafargeLocator;
